     and id = #{id}
      and assess_id = #{assessId}
      and follow_id = #{followId}
      and procurement_type = #{procurementType}
      and price = #{price}
      and procurement_date = #{procurementDate}
      and store_id = #{storeId}
      and if_old_new = #{ifOldNew}
      and entirety_price = #{entiretyPrice}
      and new_car_price = #{newCarPrice}
      and car_address = #{carAddress}
      and purchase_tax = #{purchaseTax}
      and driving_license = #{drivingLicense}
      and registration_certificate = #{registrationCertificate}
