      and car_keys = #{carKeys}
      and new_car_invoice = #{newCarInvoice}
      and quality_guarantee = #{qualityGuarantee}
      and maintenance_manual = #{maintenanceManual}
      and sali = #{sali}
      and sali_end_date = #{saliEndDate}
      and commercial_insurance = #{commercialInsurance}
      and commercial_insurance_end_date = #{commercialInsuranceEndDate}
      and commercial_insurance_price = #{commercialInsurancePrice}
      and transport_tax = #{transportTax}
      and transport_tax_end_date = #{transportTaxEndDate}
      and customer_name = #{customerName}
      and customer_tel = #{customerTel}
      and customer_source = #{customerSource}
      and remark = #{remark}
      and status = #{status}


order by id desc

    limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

