mapper.select:
SELECT
a.id,
vin,
mileage,
NAME,
begin_register_date,
color,
engine_capacity,
FUNCTION,
engine_number,
manufacture_date,
year_insurance,
plate_num,
transfer_number,
auto_nature,
create_time,
create_user,
a.STATUS,
car_photo,
certificate_photo,
other_photo,
color_cn,
region_id,
auto_brand,
auto_brand_cn,
auto_style,
auto_style_cn,
auto_series,
auto_series_cn,
edit_time,
auto_id,
o.id AS order_id,
o.price,
o.status as order_status,
o.remark,
o.assess_id
FROM
car_assess a
LEFT JOIN car_assess_order o on o.assess_id = a.id

    and a.id = #{id}
    and vin = #{vin}
    and mileage = #{mileage}
    and name = #{name}
    and begin_register_date = #{beginRegisterDate}
    and color = #{color}
    and engine_capacity = #{engineCapacity}
    and function = #{function}
    and engine_number = #{engineNumber}
    and manufacture_date = #{manufactureDate}
    and year_insurance = #{yearInsurance}
    and plate_num = #{plateNum}
    and transfer_number = #{transferNumber}
    and auto_nature = #{autoNature}
    and a.create_time = #{createTime}
    and a.create_user = #{createUser}
    and a.status = #{status}
    and car_photo = #{carPhoto}
    and certificate_photo =
        #{certificatePhoto}

    and other_photo = #{otherPhoto}
    and color_cn = #{colorCn}
    and region_id = #{regionId}
    and auto_brand = #{autoBrand}
    and auto_brand_cn = #{autoBrandCn}
    and auto_style = #{autoStyle}
    and auto_style_cn = #{autoStyleCn}
    and auto_series = #{autoSeries}
    and auto_series_cn = #{autoSeriesCn}
    and edit_time = #{editTime}
    and auto_id = #{autoId}


order by a.id desc

    limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select count(*) from car_assess

    and id = #{id}
    and vin = #{vin}
    and mileage = #{mileage}
    and name = #{name}
    and begin_register_date = #{beginRegisterDate}
    and color = #{color}
    and engine_capacity = #{engineCapacity}
    and function = #{function}
    and engine_number = #{engineNumber}
    and manufacture_date = #{manufactureDate}
    and year_insurance = #{yearInsurance}
    and plate_num = #{plateNum}
    and transfer_number = #{transferNumber}
    and auto_nature = #{autoNature}
    and create_time = #{createTime}
    and create_user = #{createUser}
    and status = #{status}
    and car_photo = #{carPhoto}
    and certificate_photo =
        #{certificatePhoto}

    and other_photo = #{otherPhoto}
    and color_cn = #{colorCn}
    and region_id = #{regionId}
    and auto_brand = #{autoBrand}
    and auto_brand_cn = #{autoBrandCn}
    and auto_style = #{autoStyle}
    and auto_style_cn = #{autoStyleCn}
    and auto_series = #{autoSeries}
    and auto_series_cn = #{autoSeriesCn}
    and edit_time = #{editTime}
    and auto_id = #{autoId}
