mapper.select:
select
from car_auction_bid_record t1 order by t1.id desc
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select

from car_auction_bid_record t1
where t1.id = #{id}

select

from car_auction_bid_record t1
where t1.auction_id = #{auctionId}

SELECT MAX(t1.bid_fee) AS bid_fee FROM car_auction_bid_record t1
WHERE t1.car_id=#{carId}

    AND t1.customer_id=#{customerId}


    AND t1.auction_car_id=#{auctionCarId}


    AND t1.auction_id=#{auctionId}

SELECT t1.bid_fee AS bid_fee FROM car_auction_bid_record t1
WHERE t1.car_id=#{carId}

    AND t1.customer_id=#{customerId}


    AND t1.auction_car_id=#{auctionCarId}


    AND t1.auction_id=#{auctionId}

SELECT t2.main_photo, t1.car_id, t2.car_auto_no, t1.add_fee, t1.bid_fee, t1.bid_time, t1.id, t1.auction_id, t2.auto_info_name, t3.vehicle_attribution_city, t3.vehicle_attribution_city_cn, t3.mileage, t2.report_servicing_ranks, t2.report_colligation_ranks, t2.status as status, t2.report_score, t2.id AS autoId, cs.simple_name as carStoreName, t4.auction_type, t4.auction_start_time, t4.auction_end_time, t4.starting_price, t4.top_bid_price max_bid_price, t7.auction_code, (CASE WHEN t5.entrust_fee IS NULL THEN 0 ELSE t5.entrust_fee END ) AS entrustPrice, (CASE WHEN o.id IS NOT NULL THEN '1' ELSE '2' END ) AS bidSuccess FROM car_auction_bid_record t1 INNER JOIN (SELECT SUBSTRING_INDEX(GROUP_CONCAT(id ORDER BY bid_time DESC),',',1) id FROM car_auction_bid_record where customer_id = #{customerId} GROUP BY car_id) cabr ON cabr.id = t1.id INNER JOIN car_auto t2 ON t1.car_id = t2.id AND t2.auto_auction_id = t1.auction_id INNER JOIN car_auto_info_detail t3 ON t3.auto_id = t1.car_id INNER JOIN car_auto_auction t4 ON t4.id = t1.auction_id LEFT JOIN car_store cs on cs.id=t2.store_id LEFT JOIN car_order o on (o.auto_auction_id=t1.auction_id AND o.customer_id=#{customerId}) LEFT JOIN car_customer_entrust_car t5 ON (t5.auto_auction_id=t1.auction_id AND t5.customer_id=#{customerId}) LEFT JOIN car_locale_auction_car t7 ON t7.car_id = t1.car_id WHERE t1.customer_id = #{customerId}
AND t4.auction_type = #{auctionType}
AND t2.status = #{status} AND t4.top_pricer_id = #{customerId}
ORDER BY t1.bid_time DESC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT
COUNT(DISTINCT t1.car_id)
FROM car_auction_bid_record t1
INNER JOIN car_auto t2 ON t1.car_id = t2.id AND t2.auto_auction_id = t1.auction_id
INNER JOIN car_auto_info_detail t3 ON t3.auto_id = t1.car_id
INNER JOIN car_auto_auction t4 ON t4.id = t2.auto_auction_id
WHERE t1.customer_id = #{customerId}

    AND t4.auction_type = #{auctionType}


    AND t2.status = #{status}
    AND t4.top_pricer_id = #{customerId}

SELECT

FROM car_auction_bid_record t1
WHERE t1.car_id=#{carId}

    AND t1.customer_id=#{customerId}


    AND t1.auction_car_id=#{auctionCarId}
    AND t1.id = (select MAX(id) FROM car_auction_bid_record where add_fee=t1.add_fee AND
    auction_car_id=#{auctionCarId})


    AND t1.auction_id = #{auctionId}

order by t1.bid_time DESC
