mapper.select:
select

  distinct


from car_auto_log

  order by ${orderByClause}


  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select l.id,l.status,l.user_name,l.user_mobile,l.time,s.status_name from car_auto_log l LEFT JOIN car_relate_status s on (s.status=l.status AND s.type='2') WHERE 1=1
AND l.auto_id=#{carId}
order by l.time DESC

select l.id,l.status,l.user_name,l.user_mobile,l.time,s.status_name,msg
from car_auto_log l
LEFT JOIN car_relate_status s on (s.status=l.status AND s.type='2')
WHERE l.auto_id=#{carId} and l.status=#{status}
order by l.time DESC
limit 0,1;
