SELECT t3.id,t3.main_photo,t3.auto_info_name,t3.car_auto_no,t3.status, t4.vehicle_attribution_city_cn,t4.mileage,t4.engine_capacity,t4.engine_capacity_unit, t4.environment_cn,t4.begin_register_date,t5.starting_price,t2.auction_code, t1.address,t1.start_time,(CASE WHEN t6.auto_id IS NOT NULL THEN '1' ELSE '2' END ) AS isFollow, t3.auto_auction_id FROM car_locale_auction_car t2 LEFT JOIN car_locale_auction t1 on t1.id=t2.auction_id LEFT JOIN car_auto t3 on t2.car_id=t3.id LEFT JOIN car_auto_info_detail t4 on t3.id=t4.auto_id LEFT JOIN car_auto_auction t5 ON t3.auto_auction_id=t5.id LEFT JOIN car_customer_follow_auto t6 ON (t2.car_id=t6.auto_id AND t6.user_id=#{customerId} AND t6.status='1') LEFT JOIN car_agent t7 on t7.car_id = t2.car_id LEFT JOIN car_agent_log t8 on t8.agent_id = t7.id WHERE t2.auction_id = #{auctionId} AND t1.status != '0'
AND t2.auction_status IN (${actionStatusArr})
AND t4.auto_brand=#{brandId}
AND t3.auto_info_name LIKE CONCAT(CONCAT('%', #{carName}),'%')
AND concat(t3.report_colligation_ranks,t3.report_servicing_ranks)=#{grade}
AND YEAR(now())-YEAR(t4.begin_register_date) ${carAge1}
AND YEAR(now())-YEAR(t4.begin_register_date) ${carAge2}
GROUP BY t2.auto_auction_id
ORDER BY t2.sort ASC
ORDER BY t5.starting_price ASC
ORDER BY t5.starting_price DESC
ORDER BY t4.begin_register_date DESC
ORDER BY t4.begin_register_date ASC
ORDER BY t2.sort ASC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

