SELECT t1.id,t1.main_photo,t1.auto_info_name,t1.car_auto_no,t1.report_score,t1.report_colligation_ranks,t1.report_servicing_ranks, t2.vehicle_attribution_city_cn,t2.mileage,t3.starting_price,t1.status, t3.auction_start_time,t3.auction_end_time,t2.environment_cn,t2.begin_register_date ,(CASE WHEN t4.auto_id IS NOT NULL THEN '1' ELSE '2' END ) AS isFollow ,(CASE WHEN t5.entrust_fee IS NOT NULL THEN '1' ELSE '2' END ) AS isEntrust ,t1.auto_auction_id,t1.transfer_flag FROM car_auto t1 INNER JOIN car_auto_info_detail t2 on t1.id=t2.auto_id INNER JOIN car_auto_auction t3 ON t1.auto_auction_id=t3.id LEFT JOIN car_customer_follow_auto t4 ON (t1.id=t4.auto_id AND t4.user_id=#{customerId} AND t4.status='1') LEFT JOIN car_customer_entrust_car t5 ON (t5.car_id=t1.id AND t1.auto_auction_id=t5.auto_auction_id AND t5.customer_id=#{customerId}) WHERE t3.auction_type='1' and (t1.status = '6' or t1.status = '7')
AND t3.auction_end_time > NOW()
AND t1.store_id=#{storeId}
AND t1.auto_info_name LIKE CONCAT(CONCAT('%', #{carName}),'%')
AND t2.auto_brand=#{brandId}
AND t1.region_id=#{regionId}
AND t1.region_id in
#{item}
AND concat(t1.report_colligation_ranks,t1.report_servicing_ranks)=#{grade}
AND YEAR(now())-YEAR(t2.begin_register_date) ${carAge1}
AND YEAR(now())-YEAR(t2.begin_register_date) ${carAge2}
AND t1.status = '7'
AND t1.status = '6'
AND date_format(NOW(), '%Y-%m-%d') = date_format(t1.publish_time, '%Y-%m-%d')
ORDER BY t3.auction_start_time ASC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

