SELECT a.id, a.auto_info_name, a.source_type, a.publish_time, a.publish_user_name, a.publish_user_mobile, a.report_colligation_ranks, a.report_servicing_ranks, a.`status`, a.car_auto_no, a.main_photo, a.if_new, a.create_time, a.auction_num, d.vin, d.engine_capacity, d.engine_capacity_unit, d.vehicle_attribution_city_cn, d.environment_cn, d.oil_supply_system, d.transmission, d.mileage, d.manufacture_date, d.begin_register_date, d.license_number, d.car_nature, aa.owner_price, d.use_nature, d.is_modification, d.original_price, aa.starting_price, aa.reserve_price, aa.service_price, aa.auction_end_time, aa.auction_type, aa.if_agent, mu.user_name AS create_user_name, au.start_time, au.title, cs.simple_name AS carStoreNme, co.transaction_fee, co.service_fee, co.agent_fee, aa.top_bid_price AS maxBidFee, co.`status` AS orderStatus, cas.status_name, aa.owner_name, aa.owner_mobile, aa.top_bid_time bid_time, ar.mobile, ar.`name`, aa.expected_feedback_time, aa.auction_start_time, a.transfer_flag, r.name AS cityName, ar.user_num as userNum, ret.sales_consultant as salesConsultant, ret.transaction_fee as transactionFee FROM car_auto a LEFT JOIN car_auto_info_detail d ON d.auto_id=a.id LEFT JOIN car_auto_auction aa on aa.id=a.auto_auction_id LEFT JOIN ( SELECT la.title,la.start_time,ac.id,ac.car_id,ac.auto_auction_id,la.city_id FROM car_locale_auction_car ac INNER JOIN car_locale_auction la on la.id=ac.auction_id )au ON a.auto_auction_id=au.auto_auction_id LEFT JOIN car_store cs on cs.id=a.store_id LEFT JOIN car_relate_status cas on (cas.status=a.status AND cas.type='2') LEFT JOIN car_manager_user mu on mu.id=a.create_user LEFT JOIN car_order co on (co.auto_auction_id=a.auto_auction_id and co.status!='8') LEFT JOIN wt_app_user ar on ar.id=aa.top_pricer_id LEFT JOIN wt_region r on au.city_id = r.id LEFT JOIN car_order_retail ret on ret.car_id = a.id and ret.sales_type = '1' WHERE a.status != '0' AND ((a.status='1' AND a.create_user=#{managerId}) OR a.status>'1')
AND a.store_id=#{storeId}
AND a.source_type=#{sourceType}
AND a.if_new=#{ifNew}
AND a.status=#{status}
AND a.sale_flag = #{saleFlag}
AND aa.auction_start_time >= #{auctionStartTimeBegin}
AND aa.auction_start_time <= #{auctionStartTimeEnd}
AND au.city_id = #{cityId}
AND au.title = #{title}
AND ret.sales_date >=#{salesStartDate}
AND ret.sales_date <=#{salesEndDate}
AND aa.auction_type=#{auctionType}
AND a.create_time >= #{startTime}
AND date_format(a.create_time, '%Y-%m-%d') <= #{endTime}
AND a.car_auto_no = #{carAutoNo}
AND (a.status='1' OR a.status='3')
AND d.vin like CONCAT(CONCAT('%', #{vin}),'%')
AND d.license_number like CONCAT(CONCAT('%', #{licenseNumber}),'%')
AND a.auto_info_name like CONCAT(CONCAT('%', #{autoInfoName}),'%')
AND a.car_auto_no like CONCAT(CONCAT('%', #{carAutoNo}),'%')
AND mu.user_name like CONCAT(CONCAT('%', #{createUserName}),'%')
and (a.store_id in
#{storeId}
or a.store_id is null)
-- GROUP BY a.id ORDER BY a.update_time DESC,a.create_time DESC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT count(a.id)
FROM car_auto a
INNER JOIN car_auto_info_detail d ON d.auto_id=a.id
LEFT JOIN (
SELECT la.title,la.start_time,ac.id,ac.car_id,ac.auto_auction_id,la.city_id
FROM
car_locale_auction_car ac
INNER JOIN car_locale_auction la on la.id=ac.auction_id
)au ON a.auto_auction_id=au.auto_auction_id
LEFT JOIN car_auto_auction aa on aa.id=a.auto_auction_id
LEFT JOIN car_manager_user mu on mu.id=a.create_user
LEFT JOIN car_order_retail ret on ret.car_id = a.id and ret.sales_type = '1'
WHERE a.status != '0' AND ((a.status='1' AND a.create_user=#{managerId}) OR a.status>'1')

    AND a.store_id=#{storeId}


    AND a.source_type=#{sourceType}


    AND a.if_new=#{ifNew}


    AND a.sale_flag = #{saleFlag}


    AND aa.auction_start_time >= #{auctionStartTimeBegin}


    AND aa.auction_start_time <= #{auctionStartTimeEnd}


    AND au.city_id = #{cityId}


    AND ret.sales_date >=#{salesStartDate}


    AND ret.sales_date <=#{salesEndDate}


    AND a.status=#{status}


    AND aa.auction_type=#{auctionType}


    AND a.create_time >= #{startTime}


    AND date_format(a.create_time, '%Y-%m-%d')<= #{endTime}


    AND a.car_auto_no = #{carAutoNo}


    AND (a.status='1' OR a.status='3')


    AND d.vin like CONCAT(CONCAT('%', #{vin}),'%')


    AND d.license_number like CONCAT(CONCAT('%', #{licenseNumber}),'%')


    AND a.auto_info_name like CONCAT(CONCAT('%', #{autoInfoName}),'%')


    AND a.car_auto_no like CONCAT(CONCAT('%', #{carAutoNo}),'%')


    AND mu.user_name like CONCAT(CONCAT('%', #{createUserName}),'%')


    and (a.store_id in

        #{storeId}

    or a.store_id is null)

