SELECT t2.id, t2.user_code, t2.user_num, t2.name AS username, t2.mobile, t5.name AS user_store_name, t3.car_auto_no, t3.auto_info_name, t6.name AS car_store_name, t4.auction_type, t4.starting_price, t1.bid_time, t1.bid_fee, t4.status auction_status, t3.store_id as storeId from car_auction_bid_record t1 INNER JOIN wt_app_user t2 ON t1.customer_id = t2.id INNER JOIN car_auto t3 ON t1.car_id = t3.id INNER JOIN car_auto_auction t4 ON t3.auto_auction_id = t4.id LEFT JOIN car_store t5 ON t5.id=t2.store_id LEFT JOIN car_store t6 ON t6.id=t3.store_id WHERE 1=1
AND (t2.user_num LIKE CONCAT(CONCAT('%', #{searchName}),'%') OR t2.name LIKE CONCAT(CONCAT('%', #{searchName}),'%') OR t2.mobile LIKE CONCAT(CONCAT('%',#{searchName}),'%') OR t2.user_code LIKE CONCAT(CONCAT('%',#{searchName}),'%'))
AND (t3.car_auto_no LIKE CONCAT(CONCAT('%', #{carSearchName}),'%') OR t3.auto_info_name LIKE CONCAT(CONCAT('%', #{carSearchName}),'%'))
AND t2.store_id=#{customerStoreId}
AND t3.region_id = #{cityId}
AND t3.store_id=#{carStoreId}
AND t4.auction_type=#{auctionType}
AND t1.bid_time >= #{beginTime}
AND t1.bid_time <= #{endTime}
AND t1.car_id=#{carId}
AND t1.car_id in
#{item}
and t3.store_id in
#{storeId}
ORDER BY t1.bid_time DESC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT COUNT(t1.car_id) from car_auction_bid_record t1 LEFT JOIN wt_app_user t2 ON t1.customer_id = t2.id LEFT JOIN car_auto t3 ON t1.car_id = t3.id LEFT JOIN car_auto_auction t4 ON t3.auto_auction_id = t4.id WHERE 1=1
AND (t2.user_num LIKE CONCAT(CONCAT('%', #{searchName}),'%') OR t2.name LIKE CONCAT(CONCAT('%', #{searchName}),'%') OR t2.mobile LIKE CONCAT(CONCAT('%',#{searchName}),'%') OR t2.user_code LIKE CONCAT(CONCAT('%',#{searchName}),'%'))
AND (t3.car_auto_no LIKE CONCAT(CONCAT('%', #{carSearchName}),'%') OR t3.auto_info_name LIKE CONCAT(CONCAT('%', #{carSearchName}),'%'))
AND t2.store_id=#{customerStoreId}
AND t3.store_id=#{carStoreId}
AND t1.car_id=#{carId}
AND t4.auction_type LIKE CONCAT(CONCAT('%',#{auctionType}),'%')
AND t1.bid_time >= #{beginTime}
AND t1.bid_time <= #{endTime}
and t3.store_id in
#{storeId}
