mapper.select:
select

from car_chaboshi_vin_data


    and model_name = #{modelName,jdbcType=VARCHAR}


    and series_name = #{seriesName,jdbcType=VARCHAR}


    and vin = #{vin,jdbcType=VARCHAR}


    and make_report_date = #{makeReportDate,jdbcType=VARCHAR}


    and report_no = #{reportNo,jdbcType=VARCHAR}


    and manufacturer = #{manufacturer,jdbcType=VARCHAR}


    and make_date = #{makeDate,jdbcType=VARCHAR}


    and production_area = #{productionArea,jdbcType=VARCHAR}


    and car_type = #{carType,jdbcType=VARCHAR}


    and transmission_type = #{transmissionType,jdbcType=VARCHAR}


    and displacement = #{displacement,jdbcType=VARCHAR}


    and effluent_standard = #{effluentStandard,jdbcType=VARCHAR}


    and car_fire_flag = #{carFireFlag,jdbcType=INTEGER}


    and car_water_flag = #{carWaterFlag,jdbcType=INTEGER}


    and car_component_records_flag = #{carComponentRecordsFlag,jdbcType=INTEGER}


    and car_construct_records_flag = #{carConstructRecordsFlag,jdbcType=INTEGER}


    and car_outside_records_flag = #{carOutsideRecordsFlag,jdbcType=INTEGER}


    and mileageIs_normal_flag = #{mileageisNormalFlag,jdbcType=INTEGER}


    and mileage_estimate = #{mileageEstimate,jdbcType=INTEGER}


    and last_main_tain_time = #{lastMainTainTime,jdbcType=VARCHAR}


    and main_tain_times = #{mainTainTimes,jdbcType=VARCHAR}


    and last_repair_time = #{lastRepairTime,jdbcType=VARCHAR}


    and mileage_every_year = #{mileageEveryYear,jdbcType=VARCHAR}


    and normal_repair_records = #{normalRepairRecords,jdbcType=VARCHAR}


    and construct_analyze_repair_records = #{constructAnalyzeRepairRecords,jdbcType=VARCHAR}


    and component_analyze_repair_records = #{componentAnalyzeRepairRecords,jdbcType=VARCHAR}


    and outside_analyze_repair_records = #{outsideAnalyzeRepairRecords,jdbcType=VARCHAR}


    and content = #{content,jdbcType=VARCHAR}


    and date = #{date,jdbcType=VARCHAR}


    and materal = #{materal,jdbcType=VARCHAR}


    and mileage = #{mileage,jdbcType=VARCHAR}


    and pay_type = #{payType,jdbcType=VARCHAR}


    and type = #{type,jdbcType=VARCHAR}


    and remark = #{remark,jdbcType=VARCHAR}


    and create_time = #{createTime,jdbcType=TIMESTAMP}
