mapper.select:
SELECT t2.main_photo, t2.auto_info_name, t3.vehicle_attribution_city, t3.vehicle_attribution_city_cn, t2.report_colligation_ranks, t2.report_servicing_ranks, t3.mileage, t4.starting_price, t4.auction_start_time, t4.auction_end_time, t4.auction_type, t2.car_auto_no, t2.id AS carId, t1.create_time, t2.status AS carStatus,t3.environment,t3.begin_register_date, cs.simple_name as carStoreName,t6.auction_code, (CASE WHEN t5.entrust_fee IS NOT NULL THEN '1' ELSE '2' END ) AS isEntrust, t2.auto_auction_id, t4.transfer_flag FROM car_customer_follow_auto t1 INNER join car_auto t2 on t1.auto_id = t2.id INNER join car_auto_info_detail t3 on t3.auto_id = t2.id INNER join car_auto_auction t4 on t4.id = t2.auto_auction_id LEFT JOIN car_store cs on cs.id=t2.store_id LEFT JOIN car_customer_entrust_car t5 ON (t1.auto_id=t5.car_id AND t5.customer_id=#{customerId}) LEFT JOIN car_locale_auction_car t6 ON t6.car_id=t1.auto_id WHERE t1.status = '1' AND t1.user_id = #{customerId} AND (t2.status = '6' OR t2.status = '7')
AND ((t4.auction_type='1' AND t4.auction_end_time > NOW()) OR (t4.auction_type='2' AND t6.auction_status in('0','1')))
AND t4.auction_type=#{auctionType}
AND t3.vehicle_attribution_city =#{cityName}
AND t2.auto_info_name LIKE CONCAT(CONCAT('%', #{carName}),'%')
AND t2.status=#{carStatus}
AND t3.auto_brand=#{brandId}
AND concat(t2.report_colligation_ranks,t2.report_servicing_ranks)=#{grade}
AND YEAR(now())-YEAR(t3.begin_register_date) ${carAge1}
AND YEAR(now())-YEAR(t3.begin_register_date) ${carAge2}
ORDER BY t1.create_time DESC
ORDER BY t4.starting_price ASC
ORDER BY t4.starting_price DESC
ORDER BY t3.begin_register_date DESC
ORDER BY t3.begin_register_date ASC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT count(t1.id)
FROM car_customer_follow_auto t1
INNER join car_auto t2 on t1.auto_id = t2.id
INNER join car_auto_info_detail t3 on t3.auto_id = t2.id
INNER join car_auto_auction t4 on t4.id = t2.auto_auction_id
LEFT JOIN car_customer_entrust_car t5 ON (t1.auto_id=t5.car_id AND t5.customer_id=#{customerId})
LEFT JOIN car_locale_auction_car t6 ON t6.car_id=t1.auto_id
WHERE t1.status = '1' and t1.user_id = #{customerId} AND (t2.status = '6' OR t2.status = '7')


  AND ((t4.auction_type='1' AND t4.auction_end_time  >  NOW()) OR (t4.auction_type='2' AND t6.auction_status in('0','1')))


  AND t4.auction_type=#{auctionType}


  AND t3.vehicle_attribution_city =#{cityName}


  AND t2.auto_info_name LIKE CONCAT(CONCAT('%', #{carName}),'%')


  AND t2.status=#{carStatus}


  AND t3.auto_brand=#{brandId}


  AND concat(t2.report_colligation_ranks,t2.report_servicing_ranks)=#{grade}


  AND YEAR(now())-YEAR(t3.begin_register_date) ${carAge1}


  AND YEAR(now())-YEAR(t3.begin_register_date) ${carAge2}



    ORDER BY t1.create_time DESC


    ORDER BY t4.starting_price ASC


    ORDER BY t4.starting_price DESC


    ORDER BY t3.begin_register_date DESC


    ORDER BY t3.begin_register_date ASC

select

from car_customer_follow_auto
where auto_id = #{autoId} and user_id = #{userId}
