mapper.select:
select

from car_locale_auction_car t1
where t1.id = #{id}

select

from car_locale_auction_car t1
where t1.car_id = #{carId}

select count(*) from ( SELECT count(t1.id) AS carNum FROM car_locale_auction_car t1 WHERE t1.auction_id = #{auctionId}
AND t1.auction_status = #{auctionStatus}
AND t1.auction_status IN (${auctionStatusArr})
group by t1.car_id) t2

SELECT t1.id, t1.car_id, t2.id as 'auction_id', t3.car_auto_no, t3.auto_info_name, t4.license_number, t3.store_name, t3.source_type, t2.start_time, t2.address, t2.title, t5.starting_price, t5.reserve_price, t5.top_bid_price, t1.auction_status, t3.publish_user_name, t3.publish_user_mobile FROM `car_locale_auction_car` t1 left join car_locale_auction t2 on t1.auction_id=t2.id left join car_auto t3 on t1.car_id=t3.id left join car_auto_info_detail t4 on t4.auto_id=t3.id left join car_auto_auction t5 on t1.auto_auction_id=t5.id WHERE 1=1
AND t2.region_id=#{cityId}
AND t2.title like CONCAT(CONCAT('%', #{title}),'%')
AND t1.auction_status in ('2','3')
AND t1.auction_status = #{status}
AND t2.start_time >= #{beginTime}
AND t2.start_time <= #{endTime}
ORDER BY t2.start_time DESC,t1.auction_code asc
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT
count(*)
FROM `car_locale_auction_car` t1
left join car_locale_auction t2 on t1.auction_id=t2.id
WHERE 1=1

    AND t2.region_id=#{cityId}


    AND t2.title like CONCAT(CONCAT('%', #{title}),'%')


    AND t2.start_time  >=  #{beginTime}


    AND t2.start_time  <=  #{endTime}
