mapper.select:
select
from car_locale_auction t1 order by t1.create_time desc
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select 

from car_locale_auction t1
where t1.id = #{id}

SELECT
,t2.user_name as create_person_name,t3.city_name
FROM car_locale_auction t1
left join car_manager_user t2 on t1.create_person=t2.id
left join wt_city t3 on t1.city_id = t3.city_id
WHERE 1=1

  AND t1.city_id=#{regionId}


  AND t1.city_id in

    #{item}



  AND t1.title like CONCAT(CONCAT('%', #{title}),'%')


  AND t1.status=#{status}


  AND t1.status  <>  '4'


  AND t1.status  <>  '4' AND t1.status  <>  '1'


  AND t1.start_time  >=  #{beginTime}


  AND t1.start_time  <=  #{endTime}


  AND t1.id = #{auctionId}

ORDER BY t1.start_time DESC

  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT count(t1.id)
FROM car_locale_auction t1
WHERE 1=1

  AND t1.city_id=#{regionId}


  AND t1.title=#{title}


  AND t1.status=#{status}


  AND t1.status  <>  '4'


  AND t1.status  <>  '4' AND t1.status  <>  '1'


  AND t1.start_time  >=  #{beginTime}


  AND t1.start_time  <=  #{endTime}

SELECT
,t2.user_name as create_person_name
FROM car_locale_auction t1
left join car_manager_user t2 on t1.create_person=t2.id
WHERE 1=1 and (t1.status='2' or t1.status='3')

  AND t1.region_id=#{regionId}


  AND t1.title like CONCAT(CONCAT('%', #{title}),'%')


  AND t1.status=#{status}


  AND t1.status  <>  '4'


  AND t1.start_time  >=  #{beginTime}


  AND t1.start_time  <=  #{endTime}

ORDER BY t1.start_time DESC

  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

SELECT count(t1.id)
FROM car_locale_auction t1
WHERE 1=1 and (t1.status='2' or t1.status='3')

  AND t1.region_id=#{regionId}


  AND t1.title=#{title}


  AND t1.status=#{status}


  AND t1.status  <>  '4'


  AND t1.start_time  >=  #{beginTime}


  AND t1.start_time  <=  #{endTime}


  AND DATE_FORMAT(t1.start_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )

SELECT count(DISTINCT t1.city_id) FROM car_locale_auction t1 WHERE (t1.`status`='2' or t1.`status`='3') AND t1.start_time >= DATE_FORMAT(NOW(),'%Y-%m-%d')
AND DATE_FORMAT(t1.start_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
AND t1.city_id=#{cityId}
AND t1.city_id in
#{item}

SELECT count(1) FROM car_locale_auction t1 WHERE (t1.`status`='2' or t1.`status`='3') AND t1.start_time >= DATE_FORMAT(NOW(),'%Y-%m-%d')
AND DATE_FORMAT(t1.start_time, '%Y%m' ) = DATE_FORMAT( CURDATE( ) , '%Y%m' )
AND t1.city_id=#{cityId}
AND t1.city_id in
#{item}

select

from car_locale_auction t1
where t1.station_real_id = #{stationRealId}
and DATE_FORMAT(t1.start_time,'%Y%m%d')=#{auctionDate}
order by t1.start_time desc limit 1

SELECT
FROM car_locale_auction t1 inner join car_locale_auction_template clat on clat.id = t1.locale_auction_template_id WHERE 1=1
AND t1.city_id=#{regionId}
AND t1.city_id in
#{item}
AND t1.title like CONCAT(CONCAT('%', #{title}),'%')
AND t1.status in
#{item}
AND date(t1.start_time) = date(NOW())
ORDER BY t1.status DESC ,t1.start_time ASC
