mapper.select:
		select `id`,`type`,`poster`,`title`,`region_id`,`address`,`gps_longitude`,`gps_latitude`,`start_time`,`corporate_agent`,`status`,`describe`,`see_car_man`,`see_car_time`,`editor`,`edit_time`,`end_time` from car_locale_auction_template

             and id = #{id} 
             and type = #{type} 
             and poster = #{poster} 
             and title LIKE CONCAT(CONCAT('%', #{title}),'%') 
             and region_id = #{regionId} 
             and address = #{address} 
             and gps_longitude = #{gpsLongitude} 
             and gps_latitude = #{gpsLatitude} 
             and start_time = #{startTime} 
             and corporate_agent = #{corporateAgent} 
             and status = #{status} 
             and describe = #{describe} 
             and see_car_phone = #{seeCarPhone} 
             and see_car_man = #{seeCarMan} 
             and see_car_time = #{seeCarTime} 
             and editor = #{editor} 
             and edit_time = #{editTime} 
             and end_time = #{endTime} 


            limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

