mapper.select:
select
count(1)
from car_manager_role_log mrl
inner join car_manager_user u on u.id = mrl.apply_id


    AND mrl.apply_id = #{applyId,jdbcType=BIGINT}


    AND mrl.status = #{status,jdbcType=VARCHAR}


    AND mrl.log_msg = #{logMsg,jdbcType=VARCHAR}


    AND mrl.create_time = #{createTime,jdbcType=TIMESTAMP}


    AND mrl.store_id = #{storeId,jdbcType=TIMESTAMP}


    AND mrl.vin = #{vin,jdbcType=TIMESTAMP}


    AND mrl.edition = #{edition,jdbcType=TIMESTAMP}


    AND mrl.vehicle_type = #{vehicleType,jdbcType=TIMESTAMP}


    AND mrl.create_person = #{createPerson,jdbcType=BIGINT}

select mrl.id,
mrl.apply_id,
mrl.apply_time,
mrl.status,
mrl.status_cn,
mrl.log_msg,
mrl.create_time,
mrl.store_id,
mrl.vin,
mrl.vehicle_type,
mrl.photo,
mrl.create_person,
case when mrl.edition = '1' then '维修版' else '综合版' end edition,
u.user_name apply_name
from car_manager_role_log mrl
inner join car_manager_user u on u.id = mrl.apply_id


    AND mrl.apply_id = #{applyId,jdbcType=BIGINT}


    AND mrl.status = #{status,jdbcType=VARCHAR}


    AND mrl.log_msg = #{logMsg,jdbcType=VARCHAR}


    AND mrl.create_time = #{createTime,jdbcType=TIMESTAMP}


    AND mrl.store_id = #{storeId,jdbcType=TIMESTAMP}


    AND mrl.vin = #{vin,jdbcType=TIMESTAMP}


    AND mrl.edition = #{edition,jdbcType=TIMESTAMP}


    AND mrl.vehicle_type = #{vehicleType,jdbcType=TIMESTAMP}


    AND mrl.create_person = #{createPerson,jdbcType=BIGINT}



  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select

from car_manager_role_log l
left join car_manager_user apply on l.apply_id = apply.id
left join car_manager_user audit on l.create_person = audit.id
left join car_store cs on l.store_id = cs.id


    AND l.apply_id = #{applyId,jdbcType=BIGINT}


    AND l.status = #{status,jdbcType=VARCHAR}


    AND l.status_cn = #{statusCn,jdbcType=VARCHAR}


    AND l.log_msg = #{logMsg,jdbcType=VARCHAR}


    AND l.create_time = #{createTime,jdbcType=TIMESTAMP}


    AND l.store_id = #{storeId,jdbcType=VARCHAR}


    AND l.vin LIKE CONCAT(CONCAT('%', #{vin,jdbcType=VARCHAR}),'%')


    AND l.search_type = #{searchType,jdbcType=VARCHAR}


    AND l.edition = #{edition,jdbcType=VARCHAR}


    AND l.vehicle_type = #{vehicleType,jdbcType=VARCHAR}


    AND l.create_person = #{createPerson,jdbcType=BIGINT}


    AND apply.user_name LIKE CONCAT(CONCAT('%', #{applyName,jdbcType=VARCHAR}),'%')


    AND audit.user_name LIKE CONCAT(CONCAT('%', #{auditName,jdbcType=VARCHAR}),'%')


order by l.status ,l.create_time desc

  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select
  count(*)
from car_manager_role_log l
left join car_manager_user apply on l.apply_id = apply.id
left join car_manager_user audit on l.create_person = audit.id


    AND l.apply_id = #{applyId,jdbcType=BIGINT}


    AND l.status = #{status,jdbcType=VARCHAR}


    AND l.status_cn = #{statusCn,jdbcType=VARCHAR}


    AND l.log_msg = #{logMsg,jdbcType=VARCHAR}


    AND l.create_time = #{createTime,jdbcType=TIMESTAMP}


    AND l.store_id = #{storeId,jdbcType=TIMESTAMP}


    AND l.vin LIKE CONCAT(CONCAT('%', #{vin,jdbcType=VARCHAR}),'%')


    AND l.search_type = #{searchType,jdbcType=VARCHAR}


    AND l.edition = #{edition,jdbcType=VARCHAR}


    AND l.vehicle_type = #{vehicleType,jdbcType=VARCHAR}


    AND l.create_person = #{createPerson,jdbcType=BIGINT}


    AND apply.user_name LIKE CONCAT(CONCAT('%', #{applyName,jdbcType=VARCHAR}),'%')


    AND audit.user_name LIKE CONCAT(CONCAT('%', #{auditName,jdbcType=VARCHAR}),'%')
