mapper.select:
select * from ( select , t2.type_name AS role_type_name,t3.role_name, (case when t1.role_type_id = 1 then '平台' when t1.role_type_id = 2 then cc.center_name when t1.role_type_id = 3 then cs.name when t1.role_type_id = 4 then cac.company_name end ) store_name from car_manager_user t1 inner join car_manager_role_type t2 on t1.role_type_id=t2.id inner join car_manager_role t3 on t1.role_id=t3.id left join car_center cc on cc.id = t1.department_id left join car_store cs on cs.id = t1.department_id left join car_agent_company cac on cac.id = t1.department_id ) t1 where 1=1
and (t1.user_name LIKE CONCAT(CONCAT('%', #{searchName}),'%') or t1.user_key LIKE CONCAT(CONCAT('%', #{searchName}),'%') or t1.user_phone LIKE CONCAT(CONCAT('%', #{searchName}),'%') or t1.store_name LIKE CONCAT(CONCAT('%', #{searchName}),'%') )
and t1.role_type_id=#{roleTypeId}
and t1.role_id=#{roleId}
and t1.department_id=#{departmentId}
and t1.user_status=#{userStatus}
and t1.user_status <> '3'
order by t1.create_time desc
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select count(t1.id) from car_manager_user t1
where 1=1

  and (t1.user_name LIKE CONCAT(CONCAT('%', #{searchName}),'%') or
  t1.user_key LIKE CONCAT(CONCAT('%', #{searchName}),'%') or
  t1.user_phone LIKE CONCAT(CONCAT('%', #{searchName}),'%'))


  and t1.role_type_id=#{roleTypeId}


  and t1.role_id=#{roleId}


  and t1.department_id=#{departmentId}


  and t1.user_status=#{userStatus}


  and t1.user_status  <>  '3'
