AND (t1.order_no like CONCAT(CONCAT('%', #{searchName}),'%') or t2.auto_info_name like CONCAT(CONCAT('%', #{searchName}),'%') or t2.car_auto_no like CONCAT(CONCAT('%', #{searchName}),'%') or t5.name like CONCAT(CONCAT('%', #{searchName}),'%') or t5.mobile like CONCAT(CONCAT('%', #{searchName}),'%'))
AND t1.car_id=#{carId}
AND t4.auction_type=#{auctionType}
AND t1.status=#{orderStatus}
AND t2.store_id=#{storeId}
AND t2.car_auto_no like CONCAT(CONCAT('%', #{autoNo}),'%')
AND t2.auto_info_name like CONCAT(CONCAT('%', #{autoName}),'%')
AND t3.license_number like CONCAT(CONCAT('%', #{licenseNumber}),'%')
AND t3.vin like CONCAT(CONCAT('%', #{vin}),'%')
AND t1.create_time >= #{orderBeginTime}
AND t1.create_time <= #{orderEndTime}
and t2.store_id in
#{storeId}
and t1.manager_evaluate = #{managerEvaluate}
and t1.user_evaluate = #{userEvaluate}
ORDER BY t1.create_time DESC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

