SELECT t1.manager_evaluate,t1.user_evaluate,aa.if_agent,aa.auction_type,t1.id,t1.status,t1.pay_fee,t1.agent_fee,t1.service_fee,t1.remark,t1.create_time,t1.order_no, t2.main_photo,t2.car_auto_no,t2.auto_info_name,t2.report_colligation_ranks,t2.report_servicing_ranks, t3.vehicle_attribution_city,t3.vehicle_attribution_city_cn,t3.mileage,rs.status_name,t1.transaction_fee, t1.amount_fee,t2.update_time as auto_update_time,t1.auto_auction_id FROM car_order t1 INNER JOIN car_auto t2 ON t1.car_id=t2.id INNER JOIN car_auto_info_detail t3 ON t2.id=t3.auto_id LEFT JOIN car_auto_auction aa on aa.id=t1.auto_auction_id LEFT JOIN car_relate_status rs on rs.status=t1.status and rs.type='4' WHERE t1.customer_id=#{customerId}
AND t1.status='1'
AND t1.status IN ('2','3','4','5','6')
AND t1.status='7'
AND t1.status='7' AND t1.user_evaluate=0
and t1.manager_evaluate = #{managerEvaluate}
and t1.user_evaluate = #{userEvaluate}
ORDER BY t1.create_time DESC
limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

