mapper.select:
select distinct

from tbl_auction_board t1
inner join tbl_board_station t2 on t1.board_real_id=t2.board_real_id
where t1.del_flag='1'

  and t2.station_id=#{stationId}


  and t2.station_real_id=#{stationRealId}


  and t1.board_name=#{boardName}


  and t1.cutting_sign=#{cuttingSign}


  and t1.board_real_id=#{boardRealId}

order by t1.create_time desc

  limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select count(distinct t1.id)
from tbl_auction_board t1
inner join tbl_board_station t2 on t1.board_real_id=t2.board_real_id
where t1.del_flag='1'

  and t2.station_id=#{stationId}


  and t2.station_real_id=#{stationRealId}


  and t1.board_name=#{boardName}


  and t1.cutting_sign=#{cuttingSign}


  and t1.board_real_id=#{boardRealId}
