mapper.select:
select
,t2.`name`,t4.auto_info_name,t4.car_auto_no,t5.license_number
from tbl_auction_log t1
LEFT JOIN wt_app_user t2 on t1.customer_id=t2.id
LEFT JOIN car_auto t4 on t4.id=t1.car_id
LEFT JOIN car_auto_info_detail t5 on t5.auto_id=t4.id
where t1.enable='0'

    AND t1.local_auction_id = #{localAuctionId}


    AND t1.board_name = #{boardName}


    AND t1.car_id = #{carId}


    and(t4.car_auto_no like CONCAT(CONCAT('%', #{carInfo}),'%')
    OR t5.license_number like CONCAT(CONCAT('%', #{carInfo}),'%')
    )

ORDER BY t1.auction_time desc

    limit #{startRowNum,jdbcType=BIGINT},#{endRowNum,jdbcType=BIGINT}

select count(t1.id)
from tbl_auction_log t1
LEFT JOIN car_auto t4 on t4.id=t1.car_id
LEFT JOIN car_auto_info_detail t5 on t5.auto_id=t4.id
where t1.enable='0'

    AND t1.local_auction_id = #{localAuctionId}


    AND t1.board_name = #{boardName}


    AND t1.car_id = #{carId}


    and(t4.car_auto_no like CONCAT(CONCAT('%', #{carInfo}),'%')
    OR t5.license_number like CONCAT(CONCAT('%', #{carInfo}),'%')
    )

select

from tbl_auction_log t1
where t1.id = #{id}

select

from tbl_auction_log t1
where t1.auction_car_id = #{auctionCarId}
